import { Component, OnInit } from '@angular/core';


import { Inventario } from './inventario';
import { InventarioService } from './inventario.service';

//Variables globales
declare var window:any

@Component({
  selector: 'app-inventario',
  templateUrl: './inventario.component.html',
  styleUrls: ['./inventario.component.css']

})
export class InventarioComponent  implements OnInit{
[x: string]: any;

  inventario: Inventario[] = [];


  constructor(private inverntarioService :  InventarioService){}

  formModal:any;
  ngOnInit(): void {
    this.formModal=new window.bootstrap.Modal(document.getElementById("modal-inv"));
      this.getInventerio() 

  }

  getInventerio(){
    this.inverntarioService.getInventerio().subscribe(
      result  => {
        for(let i = 0; i<result.length; i++){
          let inventario = result[i] as Inventario;
          this.inventario.push(inventario);
        }
        console.log(this.inventario)
      },
      error => {
        console.log("Error")
      }
    )

  }





  //Modales

  desplegarModal(){
    this.formModal.show();
  }

  cerraModal(){
    this.formModal.hide()
  }

}
