import { Component, OnInit } from '@angular/core';


import { Inventario } from './inventario';
import { InventarioService } from './inventario.service';

//Variables globales
declare var window: any


@Component({
  selector: 'app-inventario',
  templateUrl: './inventario.component.html',
  styleUrls: ['./inventario.component.css']

})
export class InventarioComponent implements OnInit {

  // inventarios:Inventario = new Inventario();
  inventario: Inventario[] = []


  constructor(private inverntarioService: InventarioService) { }

  formModal: any;
  ngOnInit(): void {
    this.formModal = new window.bootstrap.Modal(document.getElementById("modal-inv"));
    this.getInventerio()

  }

  getInventerio() {
    this.inverntarioService.getInventerio()
      .subscribe((response: any) => {
       this.inventario response;
      });

  }


  create(): void {
    console.log()
  }


  //Modales

  desplegarModal() {
    this.formModal.show();
  }

  cerraModal() {
    this.formModal.hide()
  }

}
