import { Component, OnInit } from '@angular/core';
//Variables globales
declare var window:any

@Component({
  selector: 'app-modal-inventario',
  templateUrl: './modal-inventario.component.html',
  styleUrls: ['./modal-inventario.component.css']
})
export class ModalInventarioComponent  implements OnInit{

    formModal:any;

  ngOnInit(): void {
    this.formModal=new window.bootstrap.Modal(document.getElementById("modal-inv"));


  }

   desplegarModal(){
    this.formModal.show();
  }

  cerraModal(){
    this.formModal.hide()
  }

}
<div class="modal" id="modal-inv" tabindex="-1">
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title">Modal title</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
          <p>Modal body text goes here.</p>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
          <button type="button" class="btn btn-primary">Save changes</button>
        </div>
      </div>
    </div>
  </div>
