import { Component } from '@angular/core';
import { Inventario } from './inventario';

//Variables globales
declare var window: any

@Component({
  selector: 'app-agregar-inventario',
  templateUrl: './agregar-inventario.component.html',
  styleUrls: ['./agregar-inventario.component.css']
})
export class AgregarInventarioComponent {

  inventario: Inventario = new Inventario()

  formModal: any;

  ngOnInit(): void {

    this.formModal = new window.bootstrap.Modal(document.getElementById("modal-inv"));


  }

  //Modales

  desplegarModal() {
    this.formModal.show();
  }

  cerraModal() {
    this.formModal.hide()
  }

}
