import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Inventario } from './inventario';
import { InventarioService } from './inventario.service';

declare var windows: any;

@Component({
  selector: 'app-inventario',
  templateUrl: './inventario.component.html',
  styleUrls: ['./inventario.component.css']

})
export class InventarioComponent implements OnInit {
  @ViewChild("myModalInfo", {static: false}) myModalInfo: TemplateRef<any> | undefined;
  @ViewChild("myModalConf", {static: false}) myModalConf: TemplateRef<any> | undefined;

  inventarios: Inventario[] = []

  inventario:Inventario = new Inventario();


  constructor(private inverntarioService: InventarioService ,private modalService: NgbModal) { }


  ngOnInit(): void {
    this.getInventerio()



  }

  getInventerio(): void {
    this.inverntarioService.getInventerio()
      .subscribe((response: any) => {
        this.inventarios = response
        console.log(this.inventarios)
      });

  }


